mapper.update:
        UPDATE common.bbs_post
        SET
            (
--             컬럼명을 몰라서 보고 짤 것
            )
        WHERE 1=1
            AND bbs_numb = #{bbs_numb}
            AND post_numb = #{post_numb}
            AND use_at = 'Y'

UPDATE common.bbs_post
SET
    (
        use_at = 'N'
    )
WHERE 1=1
    AND bbs_numb = #{bbs_numb}
    AND post_numb = #{post_numb}
    AND use_at = 'Y'
