mapper.update:
UPDATE common.bbs_post
SET
    (
        use_at = 'N'
    )
WHERE 1=1
    AND bbs_numb = #{bbs_numb}
    AND post_numb = #{post_numb}
    AND use_at = 'Y'

UPDATE common.bbs_cmnt
SET

        cmnt_cnts = #{cmnt_cnts}


        use_at = #{use_at}

WHERE 1=1
    AND cmnt_no = #{cmnt_no}
    AND use_at = 'Y'
